import React from 'react';
import { makeStyles, CssBaseline, createMuiTheme, ThemeProvider } from '@material-ui/core';
import Header from 'components/Header-adv/Header';
import {
  Box,
  Container,
  Grid,
  Typography,
  Button,
  TextField,
} from '@material-ui/core';
import { Formik } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const theme = createMuiTheme({
  palette: {
    primary: {
      main: "#2867B2",
    },
    background: {
      default: "#f4f5fd"
    },
  },
})

const useStyles = makeStyles({
  appMain: {
    width: '100%',
    marginTop: '100px',
    justifyContent: "center"
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  icon: {
    height: 100,
    width: 100,
    justifyContent: "center"
  },
  card: {
    justifyContent: "center"
  }
})

function AddClient() {
  const classes = useStyles();
  const history = useHistory();

  return (
    <ThemeProvider theme={theme}>
      <div>
        <Header />
        <Box
          display="flex"
          flexDirection="column"
          height="100%"
          justifyContent="center"
        >
          <Container maxWidth="sm">
            <Formik
              initialValues={{
                imgLink: '',
                name: '',
                industry: '',
                info: '',
                headquater: '',
                type: '',
                revenue: '',
                totalEmp: '',
                websiteLink: '',
                foundedYear: '',
                specialties: '',
              }}
              validationSchema={Yup.object().shape({
                imgLink: Yup.string()
                  .matches(
                    /((https?):\/\/)?(www.)?[a-z0-9]+(\.[a-z]{2,}){1,3}(#?\/?[a-zA-Z0-9#]+)*\/?(\?[a-zA-Z0-9-_]+=[a-zA-Z0-9-%]+&?)?$/,
                    'Enter correct url!'
                  )
                  .required('Logo Link is required'),
                name: Yup.string().max(255).required('Company Name is required'),
                industry: Yup.string().max(255).required('Company Industry is required'),
                info: Yup.string().max(255).required('Company Info is required'),
                headquater: Yup.string().max(255).required('Headquater Location is required'),
                type: Yup.string().max(255).required('Company Type is required'),
                revenue: Yup.string().max(255).required('Company Revenue is required'),
                totalEmp: Yup.string().max(255).required('Company Total Number of Employees is required'),
                websiteLink: Yup.string()
                  .matches(
                    /((https?):\/\/)?(www.)?[a-z0-9]+(\.[a-z]{2,}){1,3}(#?\/?[a-zA-Z0-9#]+)*\/?(\?[a-zA-Z0-9-_]+=[a-zA-Z0-9-%]+&?)?$/,
                    'Enter correct url!'
                  )
                  .required('Logo Link is required'),
                foundedYear: Yup.string().max(255).required('Company Foundation Year is required'),
                specialties: Yup.string().max(255).required('Company Specialties is required'),
              })}

              onSubmit={(values, actions) => {
                console.log("props", values)
                axios.post('http://localhost:5000/api/create-client', values).then(response => {
                  alert(response.data.message);
                  history.replace('/clientinfo');
                })
                  .catch(error => {
                    alert(error)
                  })

              }}

            >
              {({
                errors,
                handleBlur,
                handleChange,
                handleSubmit,
                handleReset,
                isSubmitting,
                touched,
                values
              }) => (
                  <form id="postad_form" onSubmit={handleSubmit} onReset={handleReset} autoComplete="off">
                    <Box className={classes.appMain}>
                      <Typography
                        color="textSecondary"
                        gutterBottom
                        variant="body2"
                      >
                        Enter the details to upload Client Information.
                  </Typography>
                    </Box>

                    <Grid
                      container
                      spacing={2}
                    >
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.name && errors.name)}
                          fullWidth
                          helperText={touched.name && errors.name}
                          label="Company Name *"
                          margin="normal"
                          name="name"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.name}
                          variant="outlined"
                        />
                      </Grid>
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.industry && errors.industry)}
                          fullWidth
                          helperText={touched.industry && errors.industry}
                          label="Company Industry *"
                          margin="normal"
                          name="industry"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.industry}
                          variant="outlined"
                        />
                      </Grid>
                    </Grid>

                    <Grid
                      container
                    // spacing={1}
                    >
                      <Grid
                        item
                        xs={12}
                      >
                        <TextField
                          error={Boolean(touched.imgLink && errors.imgLink)}
                          fullWidth
                          helperText={touched.imgLink && errors.imgLink}
                          label="Company Logo Link *"
                          margin="normal"
                          name="imgLink"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.imgLink}
                          variant="outlined"
                        />
                      </Grid>
                    </Grid>

                    <Grid
                      container
                    // spacing={1}
                    >
                      <Grid
                        item
                        xs={12}
                      >
                        <TextField
                          error={Boolean(touched.info && errors.info)}
                          fullWidth
                          multiline
                          rows={4}
                          helperText={touched.info && errors.info}
                          label="Company Info*"
                          margin="normal"
                          name="info"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.info}
                          variant="outlined"
                        />
                      </Grid>
                    </Grid>

                    <Grid
                      container
                      spacing={2}
                    >
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.headquater && errors.headquater)}
                          fullWidth
                          helperText={touched.headquater && errors.headquater}
                          label="Headquater Location *"
                          margin="normal"
                          name="headquater"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.headquater}
                          variant="outlined"
                        />
                      </Grid>
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.type && errors.type)}
                          fullWidth
                          helperText={touched.type && errors.type}
                          label="Company Type *"
                          margin="normal"
                          name="type"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.type}
                          variant="outlined"
                        />
                      </Grid>
                    </Grid>

                    <Grid
                      container
                      spacing={2}
                    >
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.revenue && errors.revenue)}
                          fullWidth
                          helperText={touched.revenue && errors.revenue}
                          label="Company Revenue in $ *"
                          margin="normal"
                          name="revenue"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.revenue}
                          variant="outlined"
                        />
                      </Grid>
                      <Grid
                        item
                        xs={6}
                      >
                        <TextField
                          error={Boolean(touched.totalEmp && errors.totalEmp)}
                          fullWidth
                          helperText={touched.totalEmp && errors.totalEmp}
                          label="Total Employess *"
                          margin="normal"
                          name="totalEmp"
                          onBlur={handleBlur}
                          onChange={handleChange}
                          value={values.totalEmp}
                          variant="outlined"
                        />
                      </Grid>
                    </Grid>

                    <Grid
                      container
                    // spacing={1}
                    >
                      <Grid
                        item
                        xs={12}
                      >
                        <TextField
                          error={Boolean(touched.websiteLink && errors.websiteLink)}
                          fullWidth
                          helperText={touched.websiteLink && errors.websiteLink}
                          label="Company Website Link *"
                          margin="normal"
                          name="websiteLink"
                          onBlur={handleBlur}
                          onChange={handleChange}
